import groovy.json.JsonSlurper
def ResponseMessage = messageExchange.response.responseContent def json = new JsonSlurper().parseText(ResponseMessage) def payload = json.payload def entity = payload.entities[0] def pagePath = payload.url.replaceFirst(/^.*\/\/.*?\//, "") def hostPath = entity.url.replaceFirst(/^.*\/\/.*?\//, "")
assert pagePath == "domains/" + entity.domainId + "/hosts?page=2&size=2" assert hostPath == "domains/" + entity.domainId + "/hosts/"+ entity.id assert payload.pageNumber == 2 assert payload.pageSize == 2 assert payload.totalCount == 3 assert payload.pageCount == 2

